import styled from 'styled-components'
import { useState, useEffect, useRef } from 'react'
import { get } from '../../../../global/Services'

const Background = styled.div`

    position: absolute;
    top: 0;
    background-color: black;
    opacity: 0.2;
    height: 100%;
    width: 100%;
    z-index: 2;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Div = styled.div`

    position: absolute;
    z-index: 3;
    height: 50%;
    width: 50%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Cancel = styled.button`

    margin-left: auto;
    margin-bottom: 0.5em;
    cursor: pointer;

`;

const DetailDiv = styled.div`

    border-style: solid;
    height: 100%;
    width: 100%;
    background-color: #edf0f2;
    color: black;
    text-align: left;

    h1{
        margin-left: 1em;
    }

`;

const UserDetail = ( { userId, setStatus } ) => {

    const refContainer = useRef(userId);
    const [DATA, setDATA] = useState(false)

    useEffect(() => {
        get(`management/users/${refContainer.current}/`).then(data => {
            setDATA(data)
        })
    }, [])

    if (DATA) {
        return (
            <>
                <Background>
                </Background>
                <Div>
                    <Cancel onClick={() => setStatus(false)}>X</Cancel>
                    <DetailDiv>
                        <h1>{DATA.username}</h1>
                        <form>
                            {() => {
                                for(let i = 0; i < DATA.allpermissions.length; i++){
                                    if(DATA.userpermissions.includes(DATA.allpermissions[i])){
                                        return(
                                            <>
                                                <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"/>
                                                <label for="vehicle1"> I have a bike</label>
                                            </>
                                        )
                                    }else{
                                        return(
                                            <>
                                                <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"/>
                                                <label for="vehicle1"> I have a bike</label>
                                            </>
                                        )
                                    }
                                }
                            }}
                        </form>
                        {JSON.stringify(DATA)}
                    </DetailDiv>
                </Div>
            </>
        )
    }else{
        return(
            <>
            <Background>
            </Background>
            <Div>
                <Cancel onClick={() => setStatus(false)}>X</Cancel>
                <DetailDiv>

                </DetailDiv>
            </Div>
        </>
        )
    }
}

export default UserDetail
