import { useState, useEffect, useRef } from 'react'
import { Link, useHistory } from 'react-router-dom'
import styled from 'styled-components'
import { checkAccessRights } from '../../global/Services'
import Header from './Header'

const Div = styled.div`

    margin-top: 1em;

    button{
        margin: 2em;
        height: 5em;
        width: 10em;
    }

`;

const Picking = () => {
    const [accessrights, setAccessrights] = useState([]);
    const history = useRef();
    history.current = useHistory();

    useEffect(() => {
        checkAccessRights().then(access => {
            if(access) {
                if(access.includes('Modules | Picking')){
                    setAccessrights(access);
                }else{
                    history.current.push('/menu');
                }
            }else{
                history.current.push('/login');
            }
        })}, [])

    const [content, setContent] = useState(false)

    return (
        <div>
            <Header/>
            {!content && <Div>
                <button onClick={() => {setContent('newPicking')}}>New Picking</button>
                <button onClick={() => {setContent('retrievePicking')}}>Retrieve Picking</button>
            </Div>}
            
        </div>
    )
}

export default Picking
