        if(response.status === 200) {
            return response.data
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}

export async function postSizing(data) {
    try{
        const token = localStorage.getItem('token');
        let response = await axios.post(`${baseUrl}management/sizing/`, data, {headers: {'Authorization': `Token ${token}`}})
        if(response.status === 200) {
            const errors = response.data.filter(obj => obj.result !== 'OK');
            return errors
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}

export async function getOnhold() {
    try{
        const token = localStorage.getItem('token');
