import styled from 'styled-components'
import { useState, useEffect, useRef } from 'react'
import { get } from '../../../../global/Services'
import { MdDelete, MdEdit, MdSave, MdCancel  } from 'react-icons/md'

const Background = styled.div`

    position: absolute;
    top: 0;
    background-color: black;
    opacity: 0.2;
    height: 100%;
    width: 100%;
    z-index: 2;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Div = styled.div`

    position: absolute;
    z-index: 3;
    height: 30%;
    width: 30%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Cancel = styled.button`

    margin-left: auto;
    margin-bottom: 0.5em;
    cursor: pointer;

`;

const DetailDiv = styled.div`

    border-style: solid;
    height: 100%;
    width: 100%;
    background-color: #f0f0f0;
    color: black;
    text-align: left;

    form{
        margin-left: 2em;
        width: 90%;
        height: 40%;
    }

    fieldset{
        height: 98%;
    }

`;

const Permissions = styled.div`

    height: 90%;
    overflow-x: auto;
    display: grid;
    grid-template-rows: 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr;
    grid-auto-flow: column;

`;

const Header = styled.div`

    margin-left: 2em;
    width: 90%;
    display: flex;
    align-items: center;

    div{
        margin-left: auto;
        margin-right: 0.5em;
        font-size: 110%;
    }

`;

const CreateUser = ( { setStatus } ) => {



    return (
        <>
            <Background/>
            <Div>
                <Cancel onClick={() => setStatus(false)}>X</Cancel>
                <DetailDiv>
                <form>
                    <fieldset disabled={!editMode}>
                        <legend>Create User</legend>

                    </fieldset>
                    </form>
                </DetailDiv>
            </Div>
        </>
    )
}

export default CreateUser
