import styled from 'styled-components'
import { useState, useEffect } from 'react'
import { post } from '../../../../global/Services'

const Background = styled.div`

    position: absolute;
    top: 0;
    background-color: black;
    opacity: 0.2;
    height: 100%;
    width: 100%;
    z-index: 2;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Div = styled.div`

    position: absolute;
    z-index: 3;
    height: 75%;
    width: 75%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;


`;

const Cancel = styled.button`

    margin-left: auto;
    margin-bottom: 0.5em;
    cursor: pointer;

`;

const DetailDiv = styled.div`

    border-style: solid;
    height: 100%;
    width: 100%;
    background-color: white;
    color: black;

`;


const Detail = ( { orderId, setStatus } ) => {

    const [DATA, setDATA] = useState([])

    const refreshPage = () => {
        post('detail/', orderId).then(data => {
            setDATA([]);
            setDATA(data)
        })
    }

    useEffect(refreshPage, [])

    return (
        <>
            <Background>
            </Background>
            <Div>
                <Cancel onClick={() => setStatus(false)}>X</Cancel>
                <DetailDiv>
                    {DATA}
                </DetailDiv>
            </Div>
        </>
    )
}

export default Detail
