import { useEffect, useRef } from 'react'
import { Link, useHistory } from 'react-router-dom'
import styled from 'styled-components'
import User from './User'
import { checkAccessRights } from './Services'

const Div = styled.div`
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: top;
    align-items: center;

    h1{
        margin-top: 10%;
    }

    div{
        border-style: solid;
        display: grid;
        grid-template-columns: 12em 12em 12em;
        justify-items: center;
        align-items: center;
    }

    button{
        margin: 5em;
        height: 5em;
        width: 8em;
    }
`;

const UserDiv = styled.div`
    position: absolute;
    right: 1em;
    top: 1em;
`;

const Main = () => {
    const accessrights = useRef();
    accessrights.current = ['asdfasdfad'];
    const history = useRef();
    history.current = useHistory();

    useEffect(() => {
        checkAccessRights().then(access => {
            access ? accessrights.current = access : history.current.push('/login');
            console.log(accessrights.current, history.current)
        })}, [])

    return (
        <>
            <UserDiv><User /></UserDiv>
            <Div>
                <h1>Main Menu</h1>
                <div>
                    {accessrights.current}
                    {('Apps | Manage' in accessrights.current) && <Link to='/manage'><button>Manage</button></Link>}
                    {('Apps | Picking' in accessrights.current) && <Link to='/picking'><button>Picking</button></Link>}
                    {('Apps | Packing' in accessrights.current) && <Link to='/packing'><button>Packing</button></Link>}
                    {('Apps | Dashboard' in accessrights.current) && <Link to='/dashboard'><button>Dashboard</button></Link>}
                </div>
            </Div>
        </>
    )
}

export default Main
