import axios from 'axios'

const baseUrl = `${window.location.origin.toString()}/api/`;

export async function checkAccessRights() {
    try{
        const token = localStorage.getItem('token');
        let response = await axios.get(`${baseUrl}authentication/accessrights/`, {headers: {'Authorization': `Token ${token}`}})
        if(response.status === 200) {
            return response.data
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}

export async function login(e, username, password) {
    let respone = await axios.post(`${baseUrl}authentication/token/`, { username: username, password: password })
    if(!('token' in respone.data)){
        console.log(respone.data);
        return false;
    }else{
        localStorage.setItem('token', respone.data.token);
        localStorage.setItem('username', username);
        return true;
    }
}

export async function getSizing() {
    try{
        const token = localStorage.getItem('token');
        let response = await axios.get(`${baseUrl}management/sizing/`, {headers: {'Authorization': `Token ${token}`}})
        if(response.status === 200) {
            return response.data
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}

export async function postSizing(data) {
    try{
        const token = localStorage.getItem('token');
        let response = await axios.post(`${baseUrl}management/sizing/`, data, {headers: {'Authorization': `Token ${token}`}})
        if(response.status === 200) {
            const errors = response.data.filter(obj => obj.result !== 'OK');
            return errors
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}
