import styled from 'styled-components'
import { useMemo, useState, useEffect } from 'react'
import { useTable } from 'react-table'
import axios from 'axios'

const Table = styled.table`
    margin-top: 20px;
    font-family: Arial, Helvetica, sans-serif;
    border-collapse: collapse;
    width: 90%;

    td, th {
        border: 1px solid #ddd;
        padding: 8px;
    }

    tr:nth-child(even){background-color: #f2f2f2;}

    tr:hover {background-color: #9b9877;}

    th {
        padding-top: 12px;
        padding-bottom: 12px;
        text-align: center;
        background-color: #4CAF50;
        color: white;
    }
`;

const COLUMNS = [
    {
        Header: 'Remote Id',
        accessor: 'remoteId'
    },
    {
        Header: 'Order Lines',
        accessor: 'productLines'
    },
    {
        Header: 'Status',
        accessor: 'status'
    },
    {
        Header: 'Commands',
        accessor: 'commands'
    },
    {
        Header: 'Size',
        accessor: 'size'
    }
]

const OrderListView = () => {

    const [DATA, setDATA] = useState([])

    const getData = () => {
        axios({
            method: 'get',
            url: 'http://127.0.0.1:8000/management/sizing'
        })
            .then(res => {console.log(typeof Object.values(res.data), Object.values(res.data));setDATA(res.data); })
            .catch(err => console.error(err));
    }

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow
    } = useTable({
        columns: COLUMNS,
        data: DATA
    })

    return (
        <Table {...getTableProps} onClick={getData}>
            <thead>
                {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map( column => (
                                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                            ))}
                        </tr>
                    ))}
            </thead>
            <tbody {...getTableBodyProps}>
                {rows.map((row) => {
                    prepareRow(row)
                    return <tr {...row.getRowProps()}>
                        {row.cells.map( cell => {
                            return <td {...cell.getCellProps}>{cell.render('Cell')}</td>
                        })}
                    </tr>
                })}
                <tr>
                    <td></td>
                </tr>
            </tbody>
        </Table>
    )
}

export default OrderListView
