import styled from 'styled-components'
import { useState, useEffect } from 'react'
import { useTable } from 'react-table'
import axios from 'axios'
import Loading from '../../../global/Loading'
import { wait } from '../../../global/Services'

const {REACT_APP_API_BASE_URL} = process.env;

//#region style

const Div = styled.div`
    width: 90%;
    height: 95%;
    overflow: auto;
    margin-top: 20px;
    display: flex;
    flex-direction: column;
    justify-content: top;
    border-style: solid;

    @media screen and (max-width: 700px){
        font-size: 70%;
    }
`;

const TableDiv = styled.div`
    max-height: 94%;
    overflow: auto;
    border-width: 0.1em;
    border-style: solid;
`;

const Table = styled.table`
    width: 100%;
    font-family: Arial, Helvetica, sans-serif;
    border-collapse: collapse;

    td, th {
        border: 1px solid #ddd;
        padding: 8px;
    }

    tr:nth-child(even){background-color: #f2f2f2;}

    tr:hover {background-color: #c0bfb0;}

    th, tfoot td{
        padding-top: 12px;
        padding-bottom: 12px;
        text-align: center;
        background-color: #4CAF50;
        color: white;
    }
`;

const Select = styled.select`
    width: 90%;
    height: 2em;
`;

const BottomDiv = styled.div`
    width: 100%;
    height: 4%;
    display: flex;
    margin-top: auto;
    margin-bottom: 0.5em;
`;

const Button = styled.button`
    width: 6em;
    height: 3em;
    margin: auto 0.5em 0 auto;

    @media screen and (max-width: 700px){
        font-size: 70%;
    }
`;

//#endregion

//#region column setup

let changes = {};

const COLUMNS = [
    {
        Header: 'Remote Id',
        Footer: 'Remote Id',
        accessor: 'remoteId'
    },
    {
        Header: 'Order',
        Footer: 'Order',
        accessor: data => {
            return (
                <>
                    {data.productLines.map(line => 
                        <p style={{textAlign: 'left', marginLeft: '1em'}}>â€¢ {line.quantity} piece(s) {line.name}. Location: {line.location}</p>
                    )}
                </>
            )
        }
    },
    {
        Header: 'Status',
        Footer: 'Status',
        accessor: 'status'
    },
    {
        Header: 'Commands',
        Footer: 'Commands',
        accessor: data => {
            return (
                <>
                    {data.commands.split('.').map(command => 
                        <p>{command}</p>)}
                </>
            )
        }
    },
    {
        Header: 'Size',
        Footer: 'Size',
        accessor: data => {
            return (
                <>
                    <Select id={data.id} name="cars" onChange={(e) => changes[e.target.id] = e.target.value} defaultValue=''>
                        <option value=""></option>
                        <option value="small">Small</option>
                        <option value="medium">Medium</option>
                        <option value="large">Large</option>
                    </Select>
                </>
            )
        }
    }
]

//#endregion

const OrderListView = () => {

    // Table setup
    const [DATA, setDATA] = useState([])
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow
    } = useTable({
        columns: COLUMNS,
        data: DATA
    })

    // refreshTable
    const refreshTable = () => {
        console.log('refresh function')
        axios({
            method: 'get',
            url: `${REACT_APP_API_BASE_URL}management/sizing`
        })
            .then(res => {changes = {}; setDATA([]); setDATA(res.data)})
            .catch(err => console.error(err));
    }

    useEffect(refreshTable, [])

    // Post sizes
    
    const [loading, setLoading] = useState([false, '', ''])

    const submit = () => {
        setLoading([true, 'Sending data to server...', 'spinner']);

        // filter data and add user info
        const filtered = {};
        for (const id in changes) {
            if(changes[id] !== ''){
                filtered[id] = changes[id]
            }
        }
        filtered['user'] = 'testuser'
        console.log(filtered)
        // sending data to server. - events will be created and event ids will return
        axios({
            method: 'post',
            url: `${REACT_APP_API_BASE_URL}management/sizing/`,
            data: filtered,
        })
            .then(res => {
                console.log(res.data);
                const errors = res.data.filter(obj => obj.result !== 'OK');
                console.log(errors)
                if(errors.length > 0) {

                    setLoading([true, `Error occured for some orders: ${errors}`, 'error']);
                    wait(2*1000).then(() => {
                        setLoading([false, '', '']);
                        refreshTable(); })
                }else {
                    setLoading([true, 'Successfull.', 'done']);
                    wait(2*1000).then(() => {
                        setLoading([false, '', '']);
                        refreshTable(); })
                }
            })
            .catch(err => {
                console.log(err);
                setLoading([true, '! SERVER ERROR !', 'error']);
                wait(2*1000).then(() => {
                    setLoading([false, '', '']);
                    refreshTable(); })
            })
    }

    return (
        <>
            {loading[0] && <Loading message={loading[1]} symbol={loading[2]}/>}
            <Div>
                <TableDiv>
                    <Table {...getTableProps}>
                        <thead>
                            {headerGroups.map((headerGroup) => (
                                    <tr {...headerGroup.getHeaderGroupProps()}>
                                        {headerGroup.headers.map( column => (
                                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                                        ))}
                                    </tr>
                                ))}
                        </thead>
                        <tbody {...getTableBodyProps}>
                            {rows.map((row) => {
                                prepareRow(row)
                                return <tr {...row.getRowProps()}>
                                    {row.cells.map( cell => (
                                        <td {...cell.getCellProps}>{cell.render('Cell')}</td>
                                    ))}
                                </tr>
                            })}
                        </tbody>
                    </Table>
                </TableDiv>
                <BottomDiv><Button onClick={submit}>Submit</Button></BottomDiv>
            </Div>
        </>
    )
}

export default OrderListView
