import { Link } from 'react-router-dom'
import styled from 'styled-components'

const Div = styled.div`
    height: 100%;
    display: flex;
    justify-content: top;
    align-items: center;
    flex-direction: column;

    form {
        width: 20em;
        border-style: solid;
        border-width: 0.1em;
        padding: 2em 1em 3em 1em;
    }

    .input {
        display: flex;
        margin: 0.5em 2em 0.5em 2em;

        input {
            margin-left: auto;
            width: 12em;
        }
    }

    #loginButon {
        margin-top: 1em;
        width: 6em;
        height: 3em;
    }
`;

const Login = () => {
    return (
        <Div>
            <form style={{textAlign: 'center', margin: 5}}>
                <legend>LeeGroup GmbH</legend>
                <p>Warehouse Management System</p>
                <hr/>
                    <div className='input'>
                        <label>Username</label>
                        <input type='text'/>
                    </div>
                    <div className='input'>
                    <label>Password</label>
                    <input type='password'/>
                    </div>
                {/* <input id='loginButon' type='submit' value='Login' onClick={(e) => e.preventDefault()}/> */}
                <Link to='/menu'><input id='loginButon' type='submit' value='Login' /></Link>
            </form>
        </Div>
    )
}

export default Login
