import { Link } from 'react-router-dom'
import styled from 'styled-components'
import axios from 'axios'

const {REACT_APP_API_BASE_URL} = process.env;

const Div = styled.div`
    height: 100%;
    display: flex;
    justify-content: top;
    align-items: center;
    flex-direction: column;

    div{
        margin-top: 10%;
    }

    form {
        width: 20em;
        border-style: solid;
        border-width: 0.1em;
        padding: 2em 1em 3em 1em;
    }

    .input {
        display: flex;
        margin: 0.5em 2em 0.5em 2em;

        input {
            margin-left: auto;
            width: 12em;
        }
    }

    #loginButon {
        margin-top: 1em;
        width: 6em;
        height: 3em;
    }
`;

const Login = () => {

    let username;
    let password;

    const submit = (e) => {
        e.preventDefault();
        axios.post(`${REACT_APP_API_BASE_URL}authentication/token/`, { username: username, password: password })
            .then(res => console.log(res.data.token));
        const pass = document.getElementById('password');
        const usr = document.getElementById('username');
        pass.value = '';
        usr.value = '';
        username = '';
        password = '';
    }

    return (
        <Div>
            <div>
                <form style={{textAlign: 'center', margin: 5}}>
                    <legend>LeeGroup GmbH</legend>
                    <p>Warehouse Management System</p>
                    <hr/>
                    <div className='input'>
                        <label>Username</label>
                        <input id='username' type='text' onChange={e => username = e.target.value}/>
                    </div>
                    <div className='input'>
                        <label>Password</label>
                        <input id='password' type='password' onChange={e => password = e.target.value}/>
                    </div>
                    <input id='loginButon' type='submit' value='Login' onClick={submit}/>
                </form>
            </div>
        </Div>
    )
}

export default Login
