import styled from 'styled-components'
import { useState, useEffect, useRef } from 'react'
import { get } from '../../../../global/Services'

const Background = styled.div`

    position: absolute;
    top: 0;
    background-color: black;
    opacity: 0.2;
    height: 100%;
    width: 100%;
    z-index: 2;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Div = styled.div`

    position: absolute;
    z-index: 3;
    height: 75%;
    width: 75%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Cancel = styled.button`

    margin-left: auto;
    margin-bottom: 0.5em;
    cursor: pointer;

`;

const DetailDiv = styled.div`

    border-style: solid;
    height: 100%;
    width: 100%;
    background-color: white;
    color: black;
    display: grid;
    grid-template-columns: 70% 30%;
`;

const Info = styled.div`

    border-style: solid;
    overflow: hidden;
    margin: 1em 1em 1em 2em;
    user-select: text;
    display: grid;
    grid-template-columns: 12px 12px 12px;
    grid-template-rows: 12px 12px 12px;

`;

const History = styled.div`

    border-style: solid;
    margin: 1em 2em 1em 1em;
    user-select: text;
    overflow-y: scroll;

`;

const Detail = ( { orderId, setStatus } ) => {

    const refContainer = useRef(orderId);
    const [DATA, setDATA] = useState(false)

    useEffect(() => {
        get(`detail/${refContainer.current}/`).then(data => {
            setDATA(data)
        })
    }, [])

    if (DATA) {
        return (
            <>
                <Background>
                </Background>
                <Div>
                    <Cancel onClick={() => setStatus(false)}>X</Cancel>
                    <DetailDiv>
                        <Info>
                            <div>
                                <label>Order ID</label>
                                <input type="text" name="id" disabled value={DATA.id}/>
                            </div>
                            <div>
                                <label>Source</label>
                                <input type="text" name="source" disabled value={DATA.source}/>
                            </div>
                            <div>
                                <label>Cache ID</label>
                                <input type="text" name="cacheId" disabled value={DATA.cacheId}/>
                            </div>
                            <div>
                                <label>Odoo Sale Order ID</label>
                                <input type="text" name="remoteId" disabled value={DATA.remoteId}/>
                            </div>
                            <div>
                                <label>Invoice Path</label>
                                <input type="text" name="invoice" disabled value={DATA.invoice}/>
                            </div>
                            <div>
                                <label>Status</label>
                                <input type="text" name="status" disabled value={DATA.status}/>
                            </div>
                            <div>
                                <label>Size</label>
                                <input type="text" name="size" disabled value={DATA.size}/>
                            </div>
                            <div>
                                <label>Commands</label>
                                <input type="text" name="commands" disabled value={DATA.commands}/>
                            </div>
                            <div>
                                <label>Customer Information</label>
                                <textarea name="shippingInformation" disabled value={`${DATA.shippingInformation.name}\n${DATA.shippingInformation.wk_company}\n${DATA.shippingInformation.street}\n${DATA.shippingInformation.zip}\n${DATA.shippingInformation.city}`}/>
                            </div>
                            <div>
                                <label>Order</label>
                                {DATA.productLines.map(line => 
                                    <p style={{textAlign: 'left', marginLeft: '1em'}}>â€¢ {line.quantity} piece(s) {line.name}. Location: {line.location}</p>)}
                            </div>
                        </Info>
                        <History>
                            {DATA.history.map(line =>
                                <>
                                    <p style={{textAlign: 'left', marginLeft: '1em', fontSize: '70%'}}>{line.time} - Status: {line.status}</p>
                                    <p style={{textAlign: 'left', marginLeft: '0.7em'}}>{line.event}</p>
                                    <hr/>
                                </>
                            )}
                        </History>
                    </DetailDiv>
                </Div>
            </>
        )
    }else{
        return(
            <>
            <Background>
            </Background>
            <Div>
                <Cancel onClick={() => setStatus(false)}>X</Cancel>
                <DetailDiv>
                    <Info>

                    </Info>
                    <History>

                    </History>
                </DetailDiv>
            </Div>
        </>
        )
    }
}

export default Detail
