import axios from 'axios'

const {REACT_APP_API_BASE_URL} = process.env;

export async function checkAccessRights() {
    try{
        const token = localStorage.getItem('token');
        let response = await axios.get(`${REACT_APP_API_BASE_URL}authentication/accessrights/`, {headers: {'Authorization': `Token ${token}`}})
        if(response.status === 200) {
;            return response.data
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}

export async function login(e, username, password) {
    let respone = await axios.post(`${REACT_APP_API_BASE_URL}authentication/token/`, { username: username, password: password })
    if(!('token' in respone.data)){
        console.log(respone.data);
        return false;
    }else{
        localStorage.setItem('token', respone.data.token);
        localStorage.setItem('username', username);
        return true;
    }
}

export function logout() {

}

export async function getSizing() {
    try{
        const token = localStorage.getItem('token');
        let response = await axios.get(`${REACT_APP_API_BASE_URL}management/sizing`, {headers: {'Authorization': `Token ${token}`}})
        if(response.status === 200) {
            return response.data
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}

export async function postSizing(data) {
    try{
        const token = localStorage.getItem('token');
        const username = localStorage.getItem('username');
        let response = await axios.post(`${REACT_APP_API_BASE_URL}management/sizing`, {headers: {'Authorization': `Token ${token}`, 'username': `${username}`}, data: data})
        if(response.status === 200) {
            const errors = response.data.filter(obj => obj.result !== 'OK');
            return errors
        }else{
            return false
        }
    }
    catch(err) {
        console.log(err);
        return false
    }
}

export function wait(ms){
    return new Promise(resolve => setTimeout(resolve, ms))
}
