import styled from 'styled-components'
import { useState, useEffect } from 'react'
import { useTable } from 'react-table'
import axios from 'axios'

const {REACT_APP_API_BASE_URL} = process.env;

//#region style

const Div = styled.div`
    width: 90%;
    height: 95%;
    overflow: auto;
    margin-top: 20px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border-style: solid;

    @media screen and (max-width: 700px){
        font-size: 70%;
    }
`;

const Table = styled.table`
    width: 100%;
    font-family: Arial, Helvetica, sans-serif;
    border-collapse: collapse;

    td, th {
        border: 1px solid #ddd;
        padding: 8px;
    }

    tr:nth-child(even){background-color: #f2f2f2;}

    tr:hover {background-color: #c0bfb0;}

    th, tfoot td{
        padding-top: 12px;
        padding-bottom: 12px;
        text-align: center;
        background-color: #4CAF50;
        color: white;
    }
`;

const Select = styled.select`
    width: 90%;
    height: 2em;
`;

const Button = styled.button`
    width: 6em;
    height: 3em;
    margin-left: auto;
`;

const BottomDiv = styled.div`
    width: 90%;
    display: flex;
    margin-top: 0.5em;
`;

//#endregion

//#region column setup

const COLUMNS = [
    {
        Header: 'Remote Id',
        Footer: 'Remote Id',
        accessor: 'remoteId'
    },
    {
        Header: 'Order',
        Footer: 'Order',
        accessor: data => {
            return (
                <>
                    {data.productLines.map(line => 
                        <p style={{textAlign: 'left', marginLeft: '1em'}}>• {line.quantity} piece(s) {line.name}. Location: {line.location}</p>
                    )}
                </>
            )
        }
    },
    {
        Header: 'Status',
        Footer: 'Status',
        accessor: 'status'
    },
    {
        Header: 'Commands',
        Footer: 'Commands',
        accessor: data => {
            return (
                <>
                    {data.commands.split('.').map(command => 
                        <p>{command}</p>)}
                </>
            )
        }
    },
    {
        Header: 'Size',
        Footer: 'Size',
        accessor: data => {
            return (
                <>
                    <Select id={data.id} name="cars">
                        <option value=""></option>
                        <option value="small">Small</option>
                        <option value="medium">Medium</option>
                        <option value="large">Large</option>
                    </Select>
                </>
            )
        }
    }
]

//#endregion

const OrderListView = () => {

    const [DATA, setDATA] = useState([])

    useEffect(() => {
        axios({
            method: 'get',
            url: `${REACT_APP_API_BASE_URL}management/sizing`
        })
            .then(res => setDATA(res.data))
            .catch(err => console.error(err));
    }, [])

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow
    } = useTable({
        columns: COLUMNS,
        data: DATA
    })

    return (
        <Div>
            <Table {...getTableProps}>
                <thead>
                    {headerGroups.map((headerGroup) => (
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {headerGroup.headers.map( column => (
                                    <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                                ))}
                            </tr>
                        ))}
                </thead>
                <tbody {...getTableBodyProps}>
                    {rows.map((row) => {
                        prepareRow(row)
                        return <tr {...row.getRowProps()}>
                            {row.cells.map( cell => {
                                return <td {...cell.getCellProps}>{cell.render('Cell')}</td>
                            })}
                        </tr>
                    })}
                </tbody>
            </Table>
            <BottomDiv><Button>Submit</Button></BottomDiv>
        </Div>
    )
}

export default OrderListView
