import { useState, useEffect, useRef } from 'react'
import { Link, useHistory } from 'react-router-dom'
import styled from 'styled-components'
import { checkAccessRights } from '../../global/Services'
import Header from './Header'

//#region style
const Outerdiv = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 95%;
`;

const Div = styled.div`
    width: 90%;
    height: 95%;
    overflow: auto;
    margin-top: 20px;
    display: flex;
    flex-direction: column;
    justify-content: top;
    border-style: solid;

    @media screen and (max-width: 700px){
        font-size: 70%;
    }
`;

const Select = styled.select`
    width: 90%;
    height: 2em;
`;

const BottomDiv = styled.div`
    width: 100%;
    height: 6%;
    display: flex;
    margin-top: auto;
    border-top: solid 0.1em;
`;

const Button = styled.button`
    width: 6em;
    margin: 0.5em 0.5em 0.5em auto;

    @media screen and (max-width: 700px){
        font-size: 70%;
    }
`;

const MenuDiv = styled.div`

    margin-top: 1em;

    button{
        margin: 2em;
        height: 5em;
        width: 10em;
    }

`;

//#endregion

const Picking = () => {
    const [accessrights, setAccessrights] = useState([]);
    const history = useRef();
    history.current = useHistory();

    useEffect(() => {
        checkAccessRights().then(access => {
            if(access) {
                if(access.includes('Modules | Picking')){
                    setAccessrights(access);
                }else{
                    history.current.push('/menu');
                }
            }else{
                history.current.push('/login');
            }
        })}, [])

    const [content, setContent] = useState(false)

    return (
        <div>
            <Header/>
            {!content && <MenuDiv>
                <button onClick={() => {setContent('newPicking')}}>New Picking</button>
                <button onClick={() => {setContent('retrievePicking')}}>Retrieve Picking</button>
            </MenuDiv>}
            {content === 'newPicking' && <Table data={DATA} columns={COLUMNS}/>}
            {content === 'retrievePicking' && <> </>}
        </div>
    )
}

export default Picking
