import { Link, useHistory } from 'react-router-dom'
import styled from 'styled-components'
import User from './User'
import { checkAccessRights } from './Services'

const Div = styled.div`
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: top;
    align-items: center;

    h1{
        margin-top: 10%;
    }

    div{
        border-style: solid;
        display: grid;
        grid-template-columns: 12em 12em 12em;
        justify-items: center;
        align-items: center;
    }

    button{
        margin: 5em;
        height: 5em;
        width: 8em;
    }
`;

const UserDiv = styled.div`
    position: absolute;
    right: 1em;
    top: 1em;
`;

const Main = () => {
    let accessrights = [];
    let history = useHistory();
    checkAccessRights().then(access => access ? accessrights = access : history.push('/login'))

    return (
        <>
            <UserDiv><User /></UserDiv>
            <Div>
                <h1>Main Menu</h1>
                <div>
                    {('Apps | Manage' in accessrights) && <Link to='/manage'><button>Manage</button></Link>}
                    {('Apps | Picking' in accessrights) && <Link to='/picking'><button>Picking</button></Link>}
                    {('Apps | Packing' in accessrights) && <Link to='/packing'><button>Packing</button></Link>}
                    {('Apps | Dashboard' in accessrights) && <Link to='/dashboard'><button>Dashboard</button></Link>}
                </div>
            </Div>
        </>
    )
}

export default Main
