import styled from 'styled-components'
import { useState, useEffect } from 'react'
import Loading from '../../../global/Loading'
import Table from './components/Table'
import { get, post } from '../../../global/Services'

//#region style
const Outerdiv = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 95%;
`;

const Div = styled.div`
    width: 90%;
    height: 95%;
    overflow: auto;
    margin-top: 20px;
    display: flex;
    flex-direction: column;
    justify-content: top;
    border-style: solid;

    @media screen and (max-width: 700px){
        font-size: 70%;
    }
`;

const Select = styled.select`
    width: 90%;
    height: 2em;
`;

const BottomDiv = styled.div`
    width: 100%;
    height: 4%;
    display: flex;
    margin-top: auto;
    margin-bottom: 0.5em;
`;

//#endregion

//#region column setup

let changes = {};

const COLUMNS = [
    {
        Header: 'Remote Id',
        Footer: 'Remote Id',
        accessor: 'remoteId'
    },
    {
        Header: 'Order',
        Footer: 'Order',
        accessor: data => {
            return (
                <>
                    {data.productLines.map(line => 
                        <p style={{textAlign: 'left', marginLeft: '1em'}}>â€¢ {line.quantity} piece(s) {line.name}. Location: {line.location}</p>
                    )}
                </>
            )
        }
    },
    {
        Header: 'Status',
        Footer: 'Status',
        accessor: 'status'
    },
    {
        Header: 'Commands',
        Footer: 'Commands',
        accessor: data => {
            return (
                <>
                    {data.commands.split('.').map(command => 
                        <p>{command}</p>)}
                </>
            )
        }
    },
    {
        Header: 'Size',
        Footer: 'Size',
        accessor: data => {
            return (
                <>
                    <Select id={data.id} name="cars" onChange={(e) => changes[e.target.id] = e.target.value} defaultValue=''>
                        <option value=""></option>
                        <option value="small">Small</option>
                        <option value="medium">Medium</option>
                        <option value="large">Large</option>
                    </Select>
                </>
            )
        }
    }
]

//#endregion

const Inprogress = () => {
    // Table
    const [DATA, setDATA] = useState([])

    const refreshTable = () => {
        getSizing().then(data => {
            changes = {};
            setDATA([]);
            setDATA(data)
        })
    }

    useEffect(refreshTable, [])

    return (
        <Outerdiv>
            <Div>
                <Table data={DATA} columns={COLUMNS}/>
                <BottomDiv>
                </BottomDiv>
            </Div>
        </Outerdiv>
    )
}

export default Inprogress
