import { useState, useEffect, useRef } from 'react'
import { Link, useHistory } from 'react-router-dom'
import styled from 'styled-components'
import { checkAccessRights } from '../../global/Services'
import Header from './Header'
import NewPicking from './content/NewPicking'

const Div = styled.div`

    margin-top: 1em;

    button{
        margin: 2em;
        height: 5em;
        width: 10em;
    }

`;

const Picking = () => {
    const [accessrights, setAccessrights] = useState([]);
    const history = useRef();
    history.current = useHistory();

    useEffect(() => {
        checkAccessRights().then(access => {
            if(access) {
                if(access.includes('Modules | Picking')){
                    setAccessrights(access);
                }else{
                    history.current.push('/menu');
                }
            }else{
                history.current.push('/login');
            }
        })}, [])

    const [content, setContent] = useState(false)

    let pickcartnumberInput;
    const [pickcartnumber, setPickcartnumber] = useState(false)
    const clickPickcartnumber = (e) => {
        e.preventDefault();
        //api validate pickcart number
    }

    return (
        <div>
            <Header/>
            {!pickcartnumber && <div>
                <label style={{margin: '0.2em'}} htmlFor='pickcartnumber'>SCAN or Enter PickCart Number</label><br/>
                <input style={{margin: '0.2em'}}  type='text' name='pickcartnumber' id='pickcartnumber' onChange={e => {pickcartnumberInput = e.target.value}}/><br/>
                <button style={{margin: '0.2em'}}  onClick={clickPickcartnumber}>Continue...</button>
            </div>}
            {content === 'newPicking' && <NewPicking/>}
            {content === 'retrievePicking' && <> </>}
        </div>
    )
}

export default Picking
