import styled from 'styled-components'
import { useState, useEffect } from 'react'
import { useTable } from 'react-table'
import axios from 'axios'

const Div = styled.div`
    width: 90%;
    max-height: 50%;
    overflow: auto;
    margin-top: 20px;
    display: flex;
    justify-content: center;
`;

const Table = styled.table`
    width: 100%;
    font-family: Arial, Helvetica, sans-serif;
    border-collapse: collapse;

    td, th {
        border: 1px solid #ddd;
        padding: 8px;
    }

    tr:nth-child(even){background-color: #f2f2f2;}

    tr:hover {background-color: #9b9877;}

    th, tfoot td{
        padding-top: 12px;
        padding-bottom: 12px;
        text-align: center;
        background-color: #4CAF50;
        color: white;
    }
`;

const COLUMNS = [
    {
        Header: 'Remote Id',
        Footer: 'Remote Id',
        accessor: 'remoteId'
    },
    {
        Header: 'Order Lines',
        Footer: 'Order Lines',
        accessor: 'productLines'
    },
    {
        Header: 'Status',
        Footer: 'Status',
        accessor: 'status'
    },
    {
        Header: 'Commands',
        Footer: 'Commands',
        accessor: 'commands'
    },
    {
        Header: 'Size',
        Footer: 'Size',
        accessor: 'size'
    }
]

const OrderListView = () => {

    const [DATA, setDATA] = useState([])

    useEffect(() => {
        axios({
            method: 'get',
            url: 'http://127.0.0.1:8000/management/sizing'
        })
            .then(res => setDATA(res.data))
            .catch(err => console.error(err));
    }, [])

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        footerGroups,
        rows,
        prepareRow
    } = useTable({
        columns: COLUMNS,
        data: DATA
    })

    return (
        <Div>
            <Table {...getTableProps}>
                <thead>
                    {headerGroups.map((headerGroup) => (
                            <tr {...headerGroup.getHeaderGroupProps()}>
                                {headerGroup.headers.map( column => (
                                    <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                                ))}
                            </tr>
                        ))}
                </thead>
                <tbody {...getTableBodyProps}>
                    {rows.map((row) => {
                        prepareRow(row)
                        return <tr {...row.getRowProps()}>
                            {row.cells.map( cell => {
                                return <td {...cell.getCellProps}>{(() => {console.log(cell); return cell.render('Cell');})()}</td>
                            })}
                        </tr>
                    })}
                </tbody>
                <tbody {...getTableBodyProps}>
                    {rows.map((row) => {
                        prepareRow(row)
                        return <tr {...row.getRowProps()}>
                            {row.cells.map( cell => {
                                return <td {...cell.getCellProps}>{(() => {console.log(cell); return cell.render('Cell');})()}</td>
                            })}
                        </tr>
                    })}
                </tbody>
                <tfoot>
                    {footerGroups.map((footerGroup) => (
                            <tr {...footerGroup.getFooterGroupProps()}>
                                {footerGroup.headers.map( column => (
                                    <td {...column.getFooterProps()}>{column.render('Footer')}</td>
                                ))}
                            </tr>
                        ))}
                </tfoot>
            </Table>
        </Div>
    )
}

export default OrderListView
