import styled from 'styled-components'
import { unmountComponentAtNode } from "react-dom";

const Background = styled.div`

    position: absolute;
    top: 0;
    background-color: red;
    opacity: 0.2;
    height: 100%;
    width: 100%;
    z-index: 2;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

`;

const Detail = () => {
    return (
        <>
            <Background>
                <button onClick={() => unmountComponentAtNode(document.getElementById('root'))}>X</button>
            </Background>
        </>
    )
}

export default Detail
